            $_index = 0;
            $_url   = ( $action . '?' );
            if ( ( ! empty( $params ) ) && ( is_array ( $params ) ) ) {
                foreach ( $params as $k => $v ) {
                    if ( is_string ( $k ) && ( ! Class_Base_Format::is_integer ( $k ) ) ) {
