            $_bin_content_length = strlen ( $bin_content );
            $_return_content     = "";
            for ( $index = 0 ; $index < $_bin_content_length ; $index ++ ) {
                $_return_content .= ( '\x' . ( str_pad ( dechex ( ord ( substr ( $bin_content , $index , 1 ) ) ) , 2 , '0' , STR_PAD_LEFT ) ) );
