    {
        $_pack_data = shmop_read ( $shmid , $offset , $length );
        $_data      = self::unpack ( $_pack_data , $format_type );
        if ( $format_type == self::DATA_FORMAT_TYPE_64_INTEGER_PACK ) {
            $_data = intval ( $_data );
