    {
        if ( is_cli () ) {
            global $_SESSION;
            if ( ! is_array ( $_SESSION ) ) {
                $_SESSION = array ();
            }
        }
        if ( ! ( Class_Base_Auth::is_enable_license_agreement () ) ) {
            if ( ! is_cli () ) {
                Class_Base_Response::redirect ( "/" );
                return null;
            } else {
                Class_Base_Auth::cli_show_license_agreement ();
            }
