    public static function exist_token ()
    {
        if ( Class_Base_Extension::exist_enabled_extensions ( Class_Base_Extension::EXTENSION_NAME_SHMOP ) ) {
            if(!is_cli()){
                $_key   = Class_Base_Auth::SHM_WEB_KEY;
            }else{
                $_key   = Class_Base_Auth::SHM_CLI_KEY;
            }
            $_shmid = Class_Base_Memory::open_share_memory ( $_key , Class_Base_Memory::MODE_SHARE_MEMORY_READ_AND_WRITE , Class_Base_Memory::BLOCK_SIZE_32 , Class_Base_Memory::FLAGS_SHARE_MEMORY_READ_AND_WRITE );
            if ( ! empty( $_shmid ) ) {
                $_share_memory_security_token = Class_Base_Memory::read_share_memory ( $_shmid , Class_Base_Memory::SHARE_MEMORY_OFFSET_START , Class_Base_Memory::BLOCK_SIZE_32 , Class_Base_Memory::DATA_FORMAT_TYPE_STRING_NULL_FILL_PACK );
                if ( ! empty( $_share_memory_security_token ) ) {
                    $_share_memory_security_token = Class_Base_Format::data_to_string ( $_share_memory_security_token );
                    if ( ! empty( $_share_memory_security_token ) ) {
                        return true;
                    }
                }
            }
        } else {
            return true;
        }

        return false;
    }

    public static function reset_token ( $password = null )
    {
        if ( Class_Base_Extension::exist_enabled_extensions ( Class_Base_Extension::EXTENSION_NAME_SHMOP ) ) {
            if(!is_cli()){
                $_key   = Class_Base_Auth::SHM_WEB_KEY;
            }else{
                $_key   = Class_Base_Auth::SHM_CLI_KEY;
            }
            $_security_code = Class_Base_Auth::SECURITY_CODE;
            $_shmid         = Class_Base_Memory::open_share_memory ( $_key , Class_Base_Memory::MODE_SHARE_MEMORY_READ_AND_WRITE , Class_Base_Memory::BLOCK_SIZE_32 , Class_Base_Memory::FLAGS_SHARE_MEMORY_OPEN );
            if ( ! empty( $_shmid ) ) {
                $_password                    = Class_Base_Auth::create_password ();
                $_security_token              = md5 ( $_password . $_security_code );
                $_share_memory_security_token = md5 ( $_security_token . $_security_code );
                $_write_length                = Class_Base_Memory::write_share_memory ( $_shmid , $_share_memory_security_token , Class_Base_Memory::SHARE_MEMORY_OFFSET_START , Class_Base_Memory::DATA_FORMAT_TYPE_STRING_NULL_FILL_PACK );
                if ( empty( $_write_length ) ) {
                    Class_Base_Memory::delete_share_memory_by_key ( $_key , Class_Base_Memory::BLOCK_SIZE_32 );
