            if ( ( strlen ( $head->get_block_string () ) != Class_Base_BlockHead::SIZE_BLOCK_HEAD ) || ( $content->get_encode_content_size () != $head->get_content_size () ) || ( strlen ( $end_flag->get_block_string () ) != Class_Base_BlockEndFlag::SIZE_BLOCK_END_FLAG ) ) {
